import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { connect } from "react-redux";
import * as actionTypes from "../../store/actions/actions";
class Form extends Component {
  render() {
    return (
      <div>
        <form onSubmit={() => this.props.onStoreResult()}>
          <TextField
            onChange={(e) => this.props.onChangeHandler(e.target.value)}
          ></TextField>
          <Button
            onClick={() => this.props.onStoreResult()}
            style={{ color: "black" }}
          >
            送信
          </Button>
        </form>
        <ul>
          {this.props.value.map((l) => (
            <li key={l.id}>{l.value}</li>
          ))}
        </ul>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    value: state.change.value,
    add: state.add.todo
  };
};
const mapDisptchToProps = (dispatch) => {
  return {
    onStoreResult: () => dispatch({ type: actionTypes.ADD }),
    onChangeHandler: (e) => dispatch({ type: actionTypes.CHANGE, value: e })
  };
};
export default connect(mapStateToProps, mapDisptchToProps)(Form);
