import {ADD,REMOVE}from '../actions/add'

const initialState = {
    add :""
}
export function counterReducer(state = initialState, action){
    switch(action.type) {
      case ADD:
        return {
          ...state,
          add: state.add.concat({id:new Date(),value:"koko"})
        };
      case REMOVE:
        return {
          ...state
        };
      default:
        return state;
    }
}
