import * as actionTypes from '../actions/actions'

const initialState = {
    todo :""
}
 const reducer =(state = initialState, action) =>{ 
    switch(action.type) {
      case actionTypes.ADD:
        alert("koko")
        return {
          ...state,
          todo: state.todo({id:new Date(),value:"koko"})
        };
      case actionTypes.REMOVE:
        return {
          ...state
        };
      default:
        return state;
    }
}

export default reducer;
