import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { connect } from "react-redux";
import * as actionTypes from "../../store/actions/actions";
class Form extends Component {
  render() {
    return (
      <div>
        <form>
          <TextField
            onChange={(e) => this.props.textChange(e.target.value)}
          ></TextField>
          <Button
            onClick={() => this.props.onStoreResult()}
            style={{ color: "black" }}
          >
            送信
          </Button>
        </form>

        <ul>
          {this.props.add.map((l) => (
            <li key={l.id}>{l.value}</li>
          ))}
        </ul>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    add: state.add.todo
  };
};
const mapDisptchToProps = (dispatch) => {
  return {
    onStoreResult: (e) =>
      dispatch({ type: actionTypes.ADD, value: e.target.value }),
    onDeleteResult: () => dispatch(actionTypes.REMOVE)
  };
};
export default connect(mapStateToProps, mapDisptchToProps)(Form);
